import styles from "./Header.module.css";
import { memo, useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import { getUserKey } from "../../utils/getUserKey";

export const Header = memo(() => {
    const [inputOpened, setInputOpened] = useState(false);
    const [apiKey, setApiKey] = useState(getUserKey());

    useEffect(() => {
        setApiKey(getUserKey());
    }, []);

    const handleKeyChange = (ev: React.ChangeEvent<HTMLInputElement>) => {
        const value = ev.target.value.trim();
        if (value.length === 40) {
            localStorage.setItem("API_KEY", value);           
            setApiKey(value);
            setInputOpened(false);  
        }
    };

    return (
        <div className={styles.container}>
            <div>
                <div className={styles.label}>ARMAGGEDON V</div>
                <div className={styles.subLabel}>
                    Сервис мониторинга и уничтожения астероидов, опасно подлетающих к Земле.
                </div>
            </div>
            <div className={styles.rightColumn}>
                <div className={styles.linkCont}>
                    <NavLink
                        to="/asteroids"
                        className={({ isActive }) =>
                            isActive ? `${styles.link} ${styles.active}` : styles.link
                        }
                    >
                        Астероиды
                    </NavLink>
                    <NavLink
                        to="/destruction"
                        className={({ isActive }) =>
                            isActive ? `${styles.link} ${styles.active}` : styles.link
                        }
                    >
                        Уничтожение
                    </NavLink>
                </div>

                <div className={styles.apiKeyBlock}>
                    {apiKey  === "DEMO_KEY" ? (
                        <button
                            className={styles.apiKeyButton}
                            onClick={() => setInputOpened(!inputOpened)}
                        >
                             Не Авторизован
                        </button>
                    ) : (
                        <div>API-ключ предоставлен</div>
                    )}
                    {inputOpened && (
                        <input
                            className={styles.apiKeyInput}
                            placeholder="Enter API Key"
                            onChange={handleKeyChange}
    />
)}
                </div>
            </div>

            <div className={styles.rectangle}></div>
        </div>
    );
});

Header.displayName = "Header";
